input[type=checkbox] {
	-webkit-appearance: textfield;
	position: relative;
	background-color: #2d2d2d;
	border-radius: 100%;
	color: #fff;
	cursor: auto;
	display: block;
	margin: 0 5px 0 0 !important;
	padding: 0;
	outline: transparent !important;
	width: 20px;
	height: 20px;
	float: left;
	clear: both;
	border: 2px solid #444;
	-moz-transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
	-o-transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
	-webkit-transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
	transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
}
input[type=checkbox]:after {
	content: '';
	display: block;
	position: absolute;
	left: 0;
	top: 0;
	width: 48px;
	height: 48px;
	margin-left: -16px;
	margin-top: -16px;
	background: #8BC34A;
	border-radius: 100%;
	opacity: .2;
	transform: scale(0);
}
@keyframes ripple {
	0% {
		transform: scale(0);
	}
	20% {
		transform: scale(1);
	}
	100% {
		opacity: 0;
		transform: scale(1);
	}
}
@keyframes rippleDuplicate {
	0% {
		transform: scale(0);
	}
	40% {
		transform: scale(1);
	}
	100% {
		opacity: 0;
		transform: scale(1);
	}
}
input[type=checkbox]:after {
	animation: ripple .4s ease-out;
}
input[type=checkbox]:checked {
	background-color: #8BC34A;
	border: 2px solid transparent;
	color: #2d2d2d;
}
input[type=checkbox]:checked:before {
	font-family: 'FontAwesome';
	color: #2d2d2d;
	content: '\f00c';
	font-size: 16px;
	line-height: 1;
	margin: 0;
	text-align: center;
	font-weight: 200;
	-moz-transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
	-o-transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
	-webkit-transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
	transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
}
