.CodeMirror .CodeMirror-gutters {
	background-color: #252525 !important;
	border-right: 1px solid #333 !important;
	box-shadow: none;
	padding-right: 0;
}
.CodeMirror .CodeMirror-linenumber {
	color: #666;
	padding: 0 0 0 5px;
	text-shadow: none;
}
.CodeMirror .CodeMirror-guttermarker {
	color: #aaa;
}
.CodeMirror .CodeMirror-guttermarker-subtle {
	color: #666;
}
.CodeMirror .CodeMirror-lines .CodeMirror-cursor {
	border-left: 1px solid #7991e8
}
.CodeMirror .CodeMirror-activeline-background {
/* 	background: rgba(24, 24, 24, 0.5) !important; */
}
.CodeMirror .CodeMirror-selected {
	background: rgba(255, 255, 255, .1) !important;
}
.CodeMirror-focused .CodeMirror-selected,
.CodeMirror::-moz-selection,
.CodeMirror::selection {
/* 	background: rgba(0, 0, 0, .15) !important; */
}
.CodeMirror-dialog {
	-moz-animation: highlight 500ms ease-out;
	-o-animation: highlight 500ms ease-out;
	-webkit-animation: highlight 500ms ease-out;
	animation: highlight 500ms ease-out;
}
.CodeMirror-dialog-top {
	border-top: 1px solid #d0a421;
	bottom: -1;
	top: inherit;
	background: #202020 !important;
}
.CodeMirror-dialog input {
	color: #d0a421;
}
@-moz-keyframes highlight {
	from {
		background-color: #d0a421;
	}
	to {
		background-color: inherit;
	}
}
@-o-keyframes highlight {
	from {
		background-color: #d0a421;
	}
	to {
		background-color: inherit;
	}
}
@-webkit-keyframes highlight {
	from {
		background-color: #d0a421;
	}
	to {
		background-color: inherit;
	}
}
@keyframes highlight {
	from {
		background-color: #d0a421;
	}
	to {
		background-color: inherit;
	}
}
.cm-searching {
	background: rgba(255, 229, 0, 0.25);
/* 	color: #fff !important; */
	padding: 2px;
	border: 1px solid rgba(255, 255, 255, 0.2);
	border-radius: 2px;
}
::-webkit-scrollbar {
	width: 8px;
}
::-webkit-scrollbar-track {
	background: transparent;
}
::-webkit-scrollbar-thumb {
	background: rgba(255, 255, 255, .1);
	border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover {
	background: rgba(255, 255, 255, .25);
}
::-webkit-scrollbar-thumb:active {
	background: rgba(255, 255, 255, .5);
}
.resize-grip {
	background: linear-gradient(-45deg, transparent 2px, #444 2px, transparent 3px, transparent 4.5px, #444 5px, transparent 5.5px);
}
.CodeMirror-search-match {
	background: #d0a421;
	border-top: 1px solid #d0a421;
	border-bottom: 1px solid #d0a421;
}
.applies-to label {
	color: #ccc;
}

/* Manage Page */
#stylish-manage #installed {
  margin-left: 300px;
	padding: 20px 0;
}
#stylish-manage #installed::after {
    background-color: transparent !important;
}
#stylish-manage #installed [style-id] {
    border-top: none;
}
#stylish-manage #installed .enabled,
#stylish-manage #installed .disabled {
	border-bottom: 1px solid #444;
	margin: 0 10px 20px 10px;
	padding: 0 15px 15px 0;
}
#stylish-manage #installed div:last-child {
	border-bottom: none !important;
}
#stylish-manage #installed div h2.style-name {
	font-weight: 300;
	font-size: 22px;
	margin: 15px 0;
}
#stylish-manage #installed div.enabled h2.style-name {
	color: #8BC34A;
}
#stylish-manage #installed div.disabled h2.style-name {
	color: #F44336;
}

/* Some Responsive Styles */

@media only screen and (min-width:641px) {

	#header {
		width: 200px;
		padding: 10px 15px;
	}
	
	#header h1, #basic-info {
		margin-bottom: 0.75rem;
	}
	
	#header h1:before {
    	font-size: 56px !important;
    	margin: 10px 0 15px 0;
	}
	
	#header h1:after {
    	font-size: 28px;
	}
	
	#actions> * {
    	padding-bottom: 20px;
	}
	
	#save-button, #beautify, #cancel-button {
    	width: 32% !important;
    	padding: 10px !important;
	}
	
	#save-button:before, #beautify:before, #cancel-button:before {
    	font-size: 20px;
    	line-height: 20px;
    	width: 20px;
    	height: 20px;
	}
	
	#from-mozilla, #to-mozilla {
		font-size: 0;
	}
	
	#from-mozilla:before, #to-mozilla:before {
    	margin-right: 0;
	}
	
	#options {
    	float: none;
		overflow: hidden;
	}
	
	img#keyMap-help {
    	right: 0px;
	}
	
	.option.aligned:last-child {
    	width: 99%;
    	margin: -1px 0 0 0;
	}
	
	.option.aligned label {
    	font-size: 12px;
	}

}

@media only screen and (min-width:1025px) {

	#header {
		width: 250px;
		padding: 15px 20px;
	}
	
	#header h1, #basic-info {
		margin-bottom: 1rem;
	}
	
	#header h1:before {
    	font-size: 64px !important;
    	margin: 10px 0 20px 0;
	}
	
	#header h1:after {
    	font-size: 32px;
	}
	
	#actions> * {
    	padding-bottom: 25px;
	}
	
	#save-button, #beautify, #cancel-button {
    	width: 32.5% !important;
    	padding: 15px !important;
	}
	
	#save-button:before, #beautify:before, #cancel-button:before {
    	font-size: 24px;
    	line-height: 24px;
    	width: 24px;
    	height: 24px;
	}
	
	#from-mozilla, #to-mozilla {
		font-size: 12px;
	}
	
	#from-mozilla:before, #to-mozilla:before {
    	margin-right: 7px;
	}
	
	#options {
    	float: left;
		overflow: visible;
	}
	
	img#keyMap-help {
    	right: -4px;
	}
	
	.option.aligned:last-child {
    	width: 98.6%;
    	margin: -1px 0 0 0;
	}
	
	.option.aligned label {
    	font-size: 13px;
	}

	
}
